import { useState } from 'react';
import './DailyCaloriesForm.css';

const DailyCaloriesForm = ({ initialValues = {} }) => {
  const [formData, setFormData] = useState({
    height: initialValues.height || '',
    age: initialValues.age || '',
    currentWeight: initialValues.currentWeight || '',
    desiredWeight: initialValues.desiredWeight || '',
    bloodType: initialValues.bloodType || '1',
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.height || formData.height <= 0) newErrors.height = 'Please enter your height in cm';
    if (!formData.age || formData.age <= 0) newErrors.age = 'Please enter your age';
    if (!formData.currentWeight || formData.currentWeight <= 0) newErrors.currentWeight = 'Please enter your weight in kg';
    if (!formData.desiredWeight || formData.desiredWeight <= 0) newErrors.desiredWeight = 'Please enter your desired weight in kg';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Form data:', formData);
    }
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Height (cm)*</label>
        <input
          type="number"
          name="height"
          value={formData.height}
          onChange={handleChange}
        />
        {errors.height && <span className="error">{errors.height}</span>}
      </div>

      <div className="form-group">
        <label>Age*</label>
        <input
          type="number"
          name="age"
          value={formData.age}
          onChange={handleChange}
        />
        {errors.age && <span className="error">{errors.age}</span>}
      </div>

      <div className="form-group">
        <label>Current weight (kg)*</label>
        <input
          type="number"
          name="currentWeight"
          value={formData.currentWeight}
          onChange={handleChange}
        />
        {errors.currentWeight && <span className="error">{errors.currentWeight}</span>}
      </div>

      <div className="form-group">
        <label>Desired weight (kg)*</label>
        <input
          type="number"
          name="desiredWeight"
          value={formData.desiredWeight}
          onChange={handleChange}
        />
        {errors.desiredWeight && <span className="error">{errors.desiredWeight}</span>}
      </div>

      <div className="form-group">
        <label>Blood type*</label>
        <div className="blood-type-options">
          {['1', '2', '3', '4'].map((type) => (
            <label key={type} className={`blood-type-label ${formData.bloodType === type ? 'active' : ''}`}>
              <input
                type="radio"
                name="bloodType"
                value={type}
                checked={formData.bloodType === type}
                onChange={handleChange}
              />
              {type}
            </label>
          ))}
        </div>
      </div>

      <button className="submit-btn" type="submit">
        Start losing weight
      </button>
    </form>
  );
};

export default DailyCaloriesForm;
