import React, { useState, useEffect } from 'react';
import DailyCaloriesForm from '../../components/DailyCaloriesForm/DailyCaloriesForm';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import Header from '../../components/Header/Header';
import './CalculatorPage.css';

const CalculatorPage = () => {
  const [userData, setUserData] = useState({
    height: '',
    age: '',
    currentWeight: '',
    desiredWeight: '',
    bloodType: '',
  });

  const [calories, setCalories] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [allForbiddenFoods, setAllForbiddenFoods] = useState([]);

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('calorieFormData'));
    if (storedData) {
      setUserData(storedData);
    }
  }, []);

  const handleCaloriesData = (newCalories, newAllForbiddenFoods) => {
    setCalories(newCalories);
    setAllForbiddenFoods(newAllForbiddenFoods);
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          <DailyCaloriesForm
            initialValues={userData}
            onCaloriesData={handleCaloriesData}
          />
        </div>
        <div className="rightPanel">
          <RightSideBar
            dailyRate={calories}
            consumed={consumed} // This will be dynamic once you implement logic
            notRecommendedFoods={allForbiddenFoods} // Pass the full list of forbidden foods
          />
        </div>
      </div>
    </>
  );
};

export default CalculatorPage;
