import React, { useState, useEffect } from 'react';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import DiaryAddProductForm from '../../components/DiaryAddProductForm/DiaryAddProductForm';
import DiaryProductsList from '../../components/DiaryProductsList/DiaryProductsList';
import Header from '../../components/Header/Header';
import './DiaryPage.css';

const DiaryPage = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const [dailyRate, setDailyRate] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [forbiddenFoods, setForbiddenFoods] = useState([]);
  const [allForbiddenFoods, setAllForbiddenFoods] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const caloriesData = JSON.parse(localStorage.getItem('caloriesData')) || {};
    const { dateCompleted = '' } = caloriesData;
    setSelectedDate(dateCompleted); // Setează data inițială

    if (caloriesData) {
      setDailyRate(caloriesData.dailyRate || 0);
      setConsumed(caloriesData.consumed || 0);
      setForbiddenFoods(caloriesData.forbiddenFoods || []);
      setAllForbiddenFoods(caloriesData.allForbiddenFoods || []);
    }
  }, []);

  const addProductToList = (product) => {
    setProducts([...products, product]);
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          <DiaryAddProductForm
            addProductToList={addProductToList}
            selectedDate={selectedDate}
            allForbiddenFoods={allForbiddenFoods}
          />
          <DiaryProductsList products={products} />
        </div>

        <RightSideBar
          dailyRate={dailyRate}
          consumed={consumed}
          allForbiddenFoods={allForbiddenFoods}
          setSelectedDate={setSelectedDate}
        />
      </div>
    </>
  );
};

export default DiaryPage;
