import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import DiaryAddProductForm from '../../components/DiaryAddProductForm/DiaryAddProductForm';
import DiaryProductsList from '../../components/DiaryProductsList/DiaryProductsList';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import Header from '../../components/Header/Header';

const DiaryPage = () => {
  const [userData, setUserData] = useState({
    height: '',
    age: '',
    currentWeight: '',
    desiredWeight: '',
    bloodType: '1',
  });

  const [dailyRate, setDailyRate] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [notRecommendedFoods, setNotRecommendedFoods] = useState([]);
  const [forbiddenFoods, setForbiddenFoods] = useState([]);
  const [allForbiddenFoods, setAllForbiddenFoods] = useState([]);
  const [searchTerm, setSearchTerm] = useState(''); // Termen de căutare pentru alimentele interzise

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('caloriesData'));
    const storedFormData = JSON.parse(localStorage.getItem('calorieFormData'));

    if (storedData) {
      setDailyRate(storedData.dailyRate || 0);
      setConsumed(storedData.consumed || 0);
      setForbiddenFoods(storedData.forbiddenFoods || []);
      setAllForbiddenFoods(storedData.allForbiddenFoods || []);
    }

    if (storedFormData) {
      setUserData(storedFormData);
    }
  }, []);

  // Filtrarea alimentelor interzise pe baza termenului de căutare
  const filteredForbiddenFoods = allForbiddenFoods.filter(food =>
    food.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          {/* Input pentru căutarea alimentelor interzise */}
          <div className="searchForbiddenFoods">
            <input
              type="text"
              placeholder="Search forbidden foods..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="searchInput"
            />
            <ul>
              {filteredForbiddenFoods.length > 0 ? (
                filteredForbiddenFoods.map((food, index) => (
                  <li key={index}>{food}</li>
                ))
              ) : (
                <p>No matching foods found</p>
              )}
            </ul>
          </div>
          <DiaryAddProductForm addProductToList={() => {}} />
          <DiaryProductsList removeProduct={() => {}} />
        </div>
        <div className="rightPanel">
          <RightSideBar
            dailyRate={dailyRate}
            consumed={consumed}
            forbiddenFoods={forbiddenFoods}
            allForbiddenFoods={allForbiddenFoods}
          />
        </div>
      </div>
    </>
  );
};

export default DiaryPage;
