import React, { useState, useEffect } from 'react';
import DailyCaloriesForm from '../../components/DailyCaloriesForm/DailyCaloriesForm';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import Header from '../../components/Header/Header';
import axios from 'axios';
import './CalculatorPage.css';

const CalculatorPage = () => {
  const [userData, setUserData] = useState({
    height: '',
    age: '',
    currentWeight: '',
    desiredWeight: '',
    bloodType: '',
  });

  const [dailyRate, setDailyRate] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [allForbiddenFoods, setAllForbiddenFoods] = useState([]);  // All forbidden foods

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('calorieFormData'));
    if (storedData) {
      setUserData(storedData);
      const caloriesData = JSON.parse(localStorage.getItem('caloriesData'));
      setDailyRate(caloriesData?.dailyRate || 2000); // 2000 is a placeholder
      setConsumed(caloriesData?.consumed || 0); // Initial consumption is 0
      set(caloriesData?.allForbiddenFoods || []); // All forbidden foods from localStorage
    }
  }, []);

  useEffect(() => {
    const fetchCalorieData = async () => {
      try {
        const token = localStorage.getItem('token');
        const isAuthenticated = !!token;  // Check if the user is logged in

        const response = await axios.post('http://localhost:5000/dailycalorieintake', {
          ...userData,
          isAuthenticated,  // Send this info to get correct forbidden foods
        });

        const { calories, allForbiddenFoods } = response.data; // Get all forbidden foods
        setDailyRate(calories);
        setAllForbiddenFoods(allForbiddenFoods);  // Set all forbidden foods
        localStorage.setItem('caloriesData', JSON.stringify({
          dailyRate: calories,
          allForbiddenFoods: allForbiddenFoods, // Save all forbidden foods to localStorage
        }));
      } catch (error) {
        console.error('Error fetching calorie data:', error);
      }
    };

    if (userData.height && userData.age && userData.currentWeight && userData.desiredWeight) {
      fetchCalorieData();  // Perform request only when data is completed
    }
  }, [userData]);

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          <DailyCaloriesForm initialValues={userData} />
        </div>
        <div className="rightPanel">
          <RightSideBar
            dailyRate={dailyRate}
            consumed={consumed}
            allForbiddenFoods={allForbiddenFoods}  // Send all forbidden foods here
          />
        </div>
      </div>
    </>
  );
};

export default CalculatorPage;
