import React, { useState, useEffect } from 'react';
import DailyCaloriesForm from '../../components/DailyCaloriesForm/DailyCaloriesForm';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import Header from '../../components/Header/Header';
import './CalculatorPage.css';

const CalculatorPage = () => {
  const [userData, setUserData] = useState({
    height: '',
    age: '',
    currentWeight: '',
    desiredWeight: '',
    bloodType: '1',
  });

  const [dailyRate, setDailyRate] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [forbiddenFoods, setForbiddenFoods] = useState([]);
  const [allForbiddenFoods, setAllForbiddenFoods] = useState([]);

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('calorieFormData')) || {};
    setUserData({
      height: storedData.height || '',
      age: storedData.age || '',
      currentWeight: storedData.currentWeight || '',
      desiredWeight: storedData.desiredWeight || '',
      bloodType: storedData.bloodType || '1',
    });
  }, []);
  
   

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          <h1>Calculate your daily calorie intake right now</h1>
          <DailyCaloriesForm initialValues={userData} />
        </div>
        <div className="rightPanel">
          <RightSideBar
            dailyRate={dailyRate}
            consumed={consumed}
            forbiddenFoods={forbiddenFoods}
            allForbiddenFoods={allForbiddenFoods}
          />
        </div>
      </div>
    </>
  );
};

export default CalculatorPage;
