// src/components/Header/Header.jsx

import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './';

const Header = () => {
  const token = localStorage.getItem('token');
  const location = useLocation();
  const isProtectedPage = location.pathname === '/diary' || location.pathname === '/calculator';

  return (
    <header className="header">
      <div className="logo">SlimMom</div>
      <nav>
        <ul>
          {token && isProtectedPage ? (
            <>
              <li><Link to="/diary">Diary</Link></li>
              <li><Link to="/calculator" className={location.pathname === '/calculator' ? 'active' : ''}>Calculator</Link></li>
            </>
          ) : (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/register">Register</Link></li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
