import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import DiaryAddProductForm from '../../components/DiaryAddProductForm/DiaryAddProductForm';
import DiaryProductsList from '../../components/DiaryProductsList/DiaryProductsList';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import Header from '../../components/Header/Header';

const DiaryPage = ({ dailyRate, consumed, allForbiddenFoods }) => {
    const navigate = useNavigate();
    const userName = localStorage.getItem('userName');
    const caloriesData = JSON.parse(localStorage.getItem('caloriesData')) || {};
    const { dateCompleted = '' } = caloriesData;
  const [products, setProducts] = useState([]);

  const filteredFoods = Array.isArray(allForbiddenFoods) 
  ? allForbiddenFoods.filter(food => food.toLowerCase().includes(searchTerm.toLowerCase())) 
  : []; 
  
  const addProductToList = (product) => {
    setProducts([...products, product]);

    // Actualizează calorii consumate
    setConsumed(consumed + product.calories);
  };

  const removeProduct = (index) => {
    const newProducts = products.filter((_, i) => i !== index);
    setProducts(newProducts);

    // Actualizează calorii consumate
    const removedProduct = products[index];
    setConsumed(consumed - removedProduct.calories);
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          <DiaryAddProductForm addProductToList={addProductToList} />
          <DiaryProductsList products={products} removeProduct={removeProduct} />
        </div>
        <div className="rightPanel">
        <div className="notRecommendedFoods">
        <h4>Food Not Recommended</h4>
        <input
          type="text"
          placeholder="Search product..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="searchInput"
        />

        <div className="food-list">
          {filteredFoods.length > 0 ? (
            <ul>
              {filteredFoods.map((food, index) => (
                <li key={index} className="food-item">
                  {food}
                </li>
              ))}
            </ul>
          ) : (
            <p>No matching products found</p>
          )}
        </div>
      </div>
        </div>
      </div>
    </>
  );
};

export default DiaryPage;
