import React, { useState, useEffect } from 'react';
  import './DiaryAddProductForm.css';
  
  const DiaryAddProductForm = ({ addProductToList, allForbiddenFoods, selectedDate  }) => {
    const [productName, setProductName] = useState('');
    const [grams, setGrams] = useState('');
    const [foodData, setFoodData] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredForbiddenFoods, setFilteredForbiddenFoods] = useState([]);
  
    useEffect(() => {
      if (productName.length > 2) {
        useEffect(() => {
            if (productName.length > 2) {
              // Căutăm alimentele care se potrivesc cu ceea ce tastează utilizatorul
              getFoodFromDatabase(productName).then(data => setFoodData(data));
            }
          }, [productName]);
        fetch(`/api/foods?name=${productName}`)
          .then(response => response.json())
          .then(data => setFoodData(data))
          .catch(error => console.error('Error fetching food data:', error));
      } else {
        setFoodData([]);
      }
    }, [productName]);
  
    useEffect(() => {
      setFilteredForbiddenFoods(
        allForbiddenFoods.filter((food) =>
          food.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    }, [searchTerm, allForbiddenFoods]);
  
    const handleSubmit = (e) => {
      e.preventDefault();
      if (!grams || !selectedFood) return;
  
      const calories = (selectedFood.calories * grams) / 100;
  
      addProductToList({
        date: selectedDate,
        name: selectedFood.title,
        grams,
        calories: Math.round(calories),
      });
  
      setProductName('');
      setGrams('');
      setSelectedFood(null);
    };
  
    const handleFoodSelect = (food) => {
      setProductName(food);
      setSearchTerm(food);
    }
  
    return (
      <form onSubmit={handleSubmit} className="productForm">
              <div className="date-picker-container">
          {/* <label htmlFor="date">Date:</label> */}
          {/* <input id="date" type="date" value={selectedDate} enabled /> */}
        </div>
        <div className={`searchForbiddenFoods ${searchTerm && filteredForbiddenFoods.length > 0 ? 'active' : ''}`}>
          <input
            type="text"
            placeholder="Enter product name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="searchInput"
          />
          <ul className="searchResults">
            {filteredForbiddenFoods.map((food, index) => (
              <li key={index} onClick={() => handleFoodSelect(food)}>{food}</li>
            ))}
          </ul>
        </div>
        <input
          type="number"
          placeholder="Grams"
          value={grams}
          onChange={(e) => setGrams(e.target.value)}
          className="gramsInput"
        />
          <button type="submit" className="addButton">+</button>
      </form>
    );
  };
  
  export default DiaryAddProductForm;
