// src/pages/CalculatorPage.jsx

import React, { useState, useEffect } from 'react';
import CalculatorCalorieForm from '../../components/CalculatorCalorieForm/CalculatorCalorieForm';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import './CalculatorPage.css';

const CalculatorPage = () => {
  const [userData, setUserData] = useState({
    height: '',
    age: '',
    currentWeight: '',
    desiredWeight: '',
    bloodType: '',
  });

  const [dailyRate, setDailyRate] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [notRecommendedFoods, setNotRecommendedFoods] = useState([]);

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('calorieFormData'));
    if (storedData) {
      setUserData(storedData);
      setDailyRate(2000); // placeholder, va fi calculat ulterior
      setConsumed(0); // inițial 0, până adăugăm produse
      setNotRecommendedFoods(['Pizza', 'Burger', 'Soda']);
    }
  }, []);

  return (
    <div className="container">
      <div className="leftPanel">
        <CalculatorCalorieForm initialValues={userData} />
      </div>
      <div className="rightPanel">
        <RightSideBar
          dailyRate={dailyRate}
          consumed={consumed}
          notRecommendedFoods={notRecommendedFoods}
        />
      </div>
    </div>
  );
};

export default CalculatorPage;
