import React from 'react';
import DailyCaloriesForm from '../components/DailyCaloriesForm/DailyCaloriesForm';
import './MainPage.css';
import Header from '../components/Header/Header';
import Background from '../components/Background/Background';

// src/components/DailyCaloriesForm.jsx

const handleSubmit = (e) => {
  e.preventDefault();
  
  const formData = {
    height,
    age,
    currentWeight,
    desiredWeight,
    bloodType,
  };

  localStorage.setItem('calorieFormData', JSON.stringify(formData));

  console.log('Data saved to localStorage:', formData);
};


const MainPage = () => {
  return (
    <div className="page-container"> {/* Container care dÄƒ context pentru z-index */}
      <Background />
      <Header />
      <main className="main">
        <h1>Calculate your daily calorie intake right now</h1>
        <DailyCaloriesForm />
      </main>
    </div>
  );
};

export default MainPage;
