const express = require('express');
const User = require('../models/Users');
const auth = require('../middleware/auth');
const router = express.Router();

// Funcția care calculează caloriile zilnice pe baza datelor utilizatorului
const calculateCalories = (weight, height, age, gender) => {
  // Poți folosi o formulă de bază pentru calcularea caloriilor
  let bmr;
  if (gender === 'male') {
    bmr = 10 * weight + 6.25 * height - 5 * age + 5;
  } else {
    bmr = 10 * weight + 6.25 * height - 5 * age - 161;
  }
  return bmr * 1.2; // Aceasta este o estimare pentru un nivel scăzut de activitate
};

app.use('/api', require('../../routes/routes/dailycalorieintake')); // presupunând că fișierul de rute se află în folderul routes

module.exports = router;
