import React, { useState, useEffect } from 'react';
import DailyCaloriesForm from '../../components/DailyCaloriesForm/DailyCaloriesForm';
import RightSideBar from '../../components/RightSideBar/RightSideBar';
import Header from '../../components/Header/Header';
import axios from 'axios';
import './CalculatorPage.css';

const CalculatorPage = () => {
  const [userData, setUserData] = useState({
    height: '',
    age: '',
    currentWeight: '',
    desiredWeight: '',
    bloodType: '',
  });

  const [dailyRate, setDailyRate] = useState(0);
  const [consumed, setConsumed] = useState(0);
  const [notRecommendedFoods, setNotRecommendedFoods] = useState([]);  // Folosește notRecommendedFoods

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('calorieFormData'));
    if (storedData) {
      setUserData(storedData);
      const caloriesData = JSON.parse(localStorage.getItem('caloriesData'));
      setDailyRate(caloriesData?.dailyRate || 2000); // 2000 este un placeholder
      setConsumed(caloriesData?.consumed || 0); // Consum începe de la 0
      setNotRecommendedFoods(caloriesData?.notRecommendedFoods || []); // Inițial, nu sunt alimente interzise
    }
  }, []);

  useEffect(() => {
    const fetchCalorieData = async () => {
      try {
        const token = localStorage.getItem('token');
        const isAuthenticated = !!token;  // Verifică dacă utilizatorul este logat

        const response = await axios.post('http://localhost:5000/dailycalorieintake', {
          ...userData,
          isAuthenticated,  // Trimite această informație pentru a obține lista corectă de alimente
        });

        const { calories, notRecommendedFoods } = response.data; // Folosește notRecommendedFoods
        setDailyRate(calories);
        setNotRecommendedFoods(notRecommendedFoods);  // Actualizează lista de alimente interzise
        localStorage.setItem('caloriesData', JSON.stringify({
          dailyRate: calories,
          notRecommendedFoods: notRecommendedFoods, // Salvează corect în localStorage
        }));
      } catch (error) {
        console.error('Error fetching calorie data:', error);
      }
    };

    if (userData.height && userData.age && userData.currentWeight && userData.desiredWeight) {
      fetchCalorieData();  // Efectuează cererea doar atunci când datele sunt completate
    }
  }, [userData]);

  return (
    <>
      <Header />
      <div className="container">
        <div className="leftPanel">
          <DailyCaloriesForm initialValues={userData} />
        </div>
        <div className="rightPanel">
          <RightSideBar
            dailyRate={dailyRate}
            consumed={consumed}
            notRecommendedFoods={notRecommendedFoods}  // Trimite lista completă de alimente interzise
          />
        </div>
      </div>
    </>
  );
};

export default CalculatorPage;
