import React, { useState } from 'react';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import './Header.css';
import { useMediaQuery } from 'react-responsive';
import logo from '../../assets/images/logo.png';

const Header = () => {
  const token = localStorage.getItem('token');
  const userName = localStorage.getItem('userName');
  const location = useLocation();
  const navigate = useNavigate();
  const isTabletOrMobile = useMediaQuery({ maxWidth: 1280 });

  const isCalculatorPage = location.pathname === '/calculator';
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedPage, setSelectedPage] = useState('');

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };

  const handlePageSelect = (page) => {
    setSelectedPage(page);
    if (page === 'DIARY') {
      navigate('/diary');
    } else if (page === 'CALCULATOR') {
      navigate('/calculator');
    }
    setModalOpen(false); // Închide modalul după selecție
  };

  return (
    <header className="header">
      <div className="header__logo">
        <img src={logo} alt="SlimMom logo" className="logo__image" />
        <span className="logo__slim">Slim</span>
        <span className="logo__mom">Mom</span>
      </div>
      <div className="nav__separator"></div>

      {/* Afișează userInfo doar pe CalculatorPage și pe mobile/tablet */}
      {isCalculatorPage && isTabletOrMobile && userName && (
        <div className="userInfoCalc">
          <span>{userName}</span>
          <span className="separator">|</span>
          
          {/* Butonul cu trei linii pentru deschiderea modalului */}
          <div className="hamburger" onClick={toggleModal}>
            <span></span>
            <span></span>
            <span></span>
          </div>
          
          <button onClick={() => window.location.href = '/'} className="exitButton">Exit</button>
        </div>
      )}

      <nav className="header__nav">
        <NavLink to="/login" className={({ isActive }) => isActive ? "nav__link active" : "nav__link"}>
          LOG IN
        </NavLink>
        <NavLink to="/register" className={({ isActive }) => isActive ? "nav__link active" : "nav__link"}>
          REGISTRATION
        </NavLink>
      </nav>

      {/* Modal pentru selectarea paginii */}
      {modalOpen && (
        <div className="modal show" onClick={toggleModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <a
              href="#"
              className={selectedPage === 'DIARY' ? 'selected' : ''}
              onClick={() => handlePageSelect('DIARY')}
            >
              DIARY
            </a>
            <a
              href="#"
              className={selectedPage === 'CALCULATOR' ? 'selected' : ''}
              onClick={() => handlePageSelect('CALCULATOR')}
            >
              CALCULATOR
            </a>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
