import React, { useState, useEffect } from 'react';
import "./DiaryProductsListItem.css"

const DiaryProductsListItem = ({ product, removeProduct }) => {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    // Adăugăm evenimentul pentru redimensionarea ferestrei
    window.addEventListener("resize", handleResize);

    // Verificăm dimensiunea ecranului la încărcarea componentei
    handleResize();

    // Curățăm evenimentul la demontarea componentei
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <li className="product-item">
      <span>{product.name}</span> - {product.grams}g - {product.calories} kcal
      <button onClick={removeProduct} className="remove-btn">
        {isMobile ? "→" : "X"} {/* Pe mobil schimbăm cu săgeată */}
      </button>
    </li>
  );
};

export default DiaryProductsListItem;
